  },
  {
    key: 'site_tagline',
    label: 'شعار الموقع',
    description: 'الشعار أو الوصف المختصر',
    type: 'string',
    defaultValue: 'نحو مستقبل تقني متقدم',
    category: 'general',
  },
  {
    key: 'contact_email',
    label: 'بريد التواصل',
    description: 'البريد الإلكتروني الرئيسي للتواصل',
    type: 'string',
    defaultValue: 'info@tda.sa',
    category: 'general',
  },
  {
    key: 'contact_phone',
    label: 'رقم الهاتف',
    description: 'رقم الهاتف الرئيسي',
    type: 'string',
    defaultValue: '+966 50 123 4567',
    category: 'general',
  },

  // Email Settings
  {
    key: 'smtp_host',
    label: 'خادم SMTP',
    description: 'عنوان خادم البريد الإلكتروني',
    type: 'string',
    defaultValue: 'smtp.zoho.com',
    category: 'email',
  },
  {
    key: 'smtp_port',
    label: 'منفذ SMTP',
    description: 'رقم المنفذ (عادة 587 أو 465)',
    type: 'number',
    defaultValue: 587,
    category: 'email',
  },
  {
    key: 'smtp_username',
    label: 'اسم المستخدم',
    description: 'البريد الإلكتروني لإرسال الرسائل',
    type: 'string',
    defaultValue: 'support@tda.sa',
    category: 'email',
  },
  {
    key: 'smtp_secure',
    label: 'اتصال آمن',
    description: 'استخدام SSL/TLS',
    type: 'boolean',
    defaultValue: true,
    category: 'email',
  },

  // SEO Settings
  {
    key: 'meta_description',
    label: 'وصف الموقع',
    description: 'وصف الموقع في محركات البحث',
    type: 'string',
    defaultValue: 'شركة التطور والتسارع التقنية - حلول تقنية متقدمة ومبتكرة',
    category: 'seo',
  },
  {
    key: 'meta_keywords',
    label: 'الكلمات المفتاحية',
    description: 'الكلمات المفتاحية للموقع',
    type: 'string',
    defaultValue: 'تطوير, برمجة, تقنية, حلول, TDA',
    category: 'seo',
  },

  // Security Settings
  {
    key: 'maintenance_mode',
    label: 'وضع الصيانة',
    description: 'تفعيل وضع الصيانة للموقع',
    type: 'boolean',
    defaultValue: false,
    category: 'security',
  },
  {
    key: 'allow_registration',
    label: 'السماح بالتسجيل',
    description: 'السماح للمستخدمين الجدد بإنشاء حسابات',
    type: 'boolean',
    defaultValue: false,
    category: 'security',
  },
];

export default function SettingsManager() {
  const [activeCategory, setActiveCategory] = useState('general');
  const [testEmail, setTestEmail] = useState('');
  const { toast } = useToast();

  const form = useForm();

  // Fetch email settings from database
  const { data: emailSettings = {}, isLoading: emailLoading } = useQuery({
    queryKey: ['/api/admin/email-settings'],
  });

  // Fetch site settings
  const { data: settings = {}, isLoading } = useQuery({
    queryKey: ['/api/admin/settings'],
  }) as { data: Record<string, { value: any; type: string }>, isLoading: boolean };

  // Update setting mutation
  const updateSettingMutation = useMutation({
    mutationFn: ({ key, value, type }: { key: string; value: any; type: string }) =>
      apiRequest('PUT', `/api/admin/settings/${key}`, { value, type }),
    onSuccess: (data, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/settings'] });
      toast({
        title: 'تم تحديث الإعداد بنجاح',
        description: `تم حفظ ${variables.key}`,
      });
    },
    onError: (error: any) => {
      toast({
        title: 'خطأ في حفظ الإعداد',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  // Test email mutation
  const testEmailMutation = useMutation({
    mutationFn: ({ testEmail }: { testEmail: string }) =>
      apiRequest('POST', '/api/admin/test-email', { testEmail }),
    onSuccess: (data: any) => {
      toast({
        title: 'نجح الاختبار! ✅',
        description: data.message,
        variant: 'default',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'فشل الاختبار ❌',
        description: error.details || error.message,
        variant: 'destructive',
      });
    },
  });

  const handleSettingChange = (setting: SettingField, value: any) => {
    updateSettingMutation.mutate({
      key: setting.key,
      value,
      type: setting.type,
    });
  };

  const getSettingValue = (setting: SettingField) => {
    return settings[setting.key]?.value ?? setting.defaultValue;
  };

  const renderSettingField = (setting: SettingField) => {
    const currentValue = getSettingValue(setting);

    switch (setting.type) {
      case 'boolean':
        return (
          <div className="flex items-center justify-between">
            <div>
              <label className="text-sm font-medium">{setting.label}</label>
              {setting.description && (
                <p className="text-xs text-gray-600 mt-1">{setting.description}</p>
              )}
            </div>
            <Switch
              checked={currentValue}
              onCheckedChange={(checked) => handleSettingChange(setting, checked)}
              data-testid={`setting-${setting.key}`}
            />
          </div>
        );

      case 'number':
        return (
          <div>
            <label className="text-sm font-medium">{setting.label}</label>
            {setting.description && (
              <p className="text-xs text-gray-600 mb-2">{setting.description}</p>
            )}
            <Input
              type="number"
              value={currentValue}
              onChange={(e) => handleSettingChange(setting, parseInt(e.target.value) || 0)}
              data-testid={`setting-${setting.key}`}
            />
          </div>
        );

      default:
        return (
          <div>
            <label className="text-sm font-medium">{setting.label}</label>
            {setting.description && (
              <p className="text-xs text-gray-600 mb-2">{setting.description}</p>
            )}
            {setting.key.includes('description') || setting.key.includes('keywords') ? (
              <Textarea
                value={currentValue}
                onChange={(e) => handleSettingChange(setting, e.target.value)}
                data-testid={`setting-${setting.key}`}
              />
            ) : (
              <Input
                value={currentValue}
                onChange={(e) => handleSettingChange(setting, e.target.value)}
                data-testid={`setting-${setting.key}`}
              />
            )}
          </div>
        );
    }
  };

  const getCategorySettings = (category: string) => {
    return defaultSettings.filter(setting => setting.category === category);
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'general': return <Globe className="w-4 h-4" />;
      case 'email': return <Mail className="w-4 h-4" />;
      case 'seo': return <Settings className="w-4 h-4" />;
      case 'security': return <Shield className="w-4 h-4" />;
      default: return <Settings className="w-4 h-4" />;
    }
  };

  if (isLoading) {
    return (
      <div className="text-center py-8">
        <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-purple-600 mx-auto"></div>
        <p className="mt-2 text-sm text-gray-600">جاري التحميل...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <h2 className="text-xl font-semibold">إعدادات النظام</h2>

      <Tabs value={activeCategory} onValueChange={setActiveCategory}>
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="general" className="flex items-center gap-2">
            {getCategoryIcon('general')}
            عام
          </TabsTrigger>
          <TabsTrigger value="email" className="flex items-center gap-2">
            {getCategoryIcon('email')}
            البريد
          </TabsTrigger>
          <TabsTrigger value="seo" className="flex items-center gap-2">
            {getCategoryIcon('seo')}
            SEO
          </TabsTrigger>
          <TabsTrigger value="security" className="flex items-center gap-2">
            {getCategoryIcon('security')}
            الأمان
          </TabsTrigger>
        </TabsList>

        <TabsContent value="general">
          <Card>
            <CardHeader>
              <CardTitle>الإعدادات العامة</CardTitle>
              <CardDescription>إعدادات الموقع الأساسية</CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              {getCategorySettings('general').map(setting => (
                <div key={setting.key}>
                  {renderSettingField(setting)}
                </div>
              ))}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="email">
          <Card>
            <CardHeader>
              <CardTitle>إعدادات البريد الإلكتروني</CardTitle>
              <CardDescription>إعدادات SMTP المحفوظة في قاعدة البيانات</CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              {emailLoading ? (
                <p>جاري تحميل الإعدادات...</p>
              ) : (
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="text-sm font-medium">مزود الخدمة</label>
                      <Input value={emailSettings.provider || ''} disabled className="mt-1" />
                    </div>
                    <div>
                      <label className="text-sm font-medium">خادم SMTP</label>
                      <Input value={emailSettings.smtpHost || ''} disabled className="mt-1" />
                    </div>
                    <div>
                      <label className="text-sm font-medium">المنفذ</label>
                      <Input value={emailSettings.smtpPort || ''} disabled className="mt-1" />
                    </div>
                    <div>
                      <label className="text-sm font-medium">اسم المستخدم</label>
                      <Input value={emailSettings.smtpUsername || ''} disabled className="mt-1" />
                    </div>
                    <div>
                      <label className="text-sm font-medium">البريد المرسل</label>
                      <Input value={emailSettings.fromEmail || ''} disabled className="mt-1" />
                    </div>
                    <div>
                      <label className="text-sm font-medium">اسم المرسل</label>
                      <Input value={emailSettings.fromName || ''} disabled className="mt-1" />
                    </div>
                  </div>

                  <div className="flex items-center gap-2">
                    <span className="text-sm">اتصال آمن:</span>
                    <Switch checked={emailSettings.smtpSecure} disabled />
                    <span className="text-xs text-gray-500">
                      {emailSettings.smtpSecure ? 'SSL/TLS مُفعّل' : 'SSL/TLS غير مُفعّل'}
                    </span>
                  </div>
                </div>
              )}

              {/* Email Test Section */}
              <div className="border-t pt-6">
                <h3 className="text-lg font-medium mb-4">اختبار إعدادات البريد الإلكتروني</h3>
                <p className="text-sm text-gray-600 mb-4">
                  أرسل رسالة تجريبية للتأكد من صحة إعدادات SMTP
                </p>
                <div className="flex gap-3">
                  <Input
                    type="email"
                    placeholder="البريد الإلكتروني للاختبار"
                    value={testEmail}
                    onChange={(e) => setTestEmail(e.target.value)}
                    className="flex-1"
                    data-testid="test-email-input"
                  />
                  <Button
                    onClick={() => {
                      if (!testEmail) {
